import { onMount } from 'svelte';

    // Test questions data
    const placementQuestions = [
        {
            id: 1,
            question: "What is the correct form of the verb 'to be' in this sentence: 'She ___ a teacher.'",
            options: ["am", "is", "are", "be"],
            correctAnswer: 1
        },
        {
            id: 2,
            question: "Choose the correct article: '___ apple a day keeps the doctor away.'",
            options: ["A", "An", "The", "No article needed"],
            correctAnswer: 1
        },
        {
            id: 3,
            question: "Which sentence is grammatically correct?",
            options: [
                "I have went to the store",
                "I have gone to the store", 
                "I have go to the store",
                "I have going to the store"
            ],
            correctAnswer: 1
        },
        {
            id: 4,
            question: "What is the past tense of 'run'?",
            options: ["runned", "ran", "run", "running"],
            correctAnswer: 1
        },
        {
            id: 5,
            question: "Choose the correct preposition: 'The book is ___ the table.'",
            options: ["in", "at", "on", "by"],
            correctAnswer: 2
        }
    ];

    const weeklyQuestions = [
        {
            id: 1,
            question: "Which word is a synonym for 'happy'?",
            options: ["sad", "joyful", "angry", "tired"],
            correctAnswer: 1
        },
        {
            id: 2,
            question: "What type of word is 'quickly' in the sentence 'She ran quickly'?",
            options: ["noun", "verb", "adjective", "adverb"],
            correctAnswer: 3
        },
        {
            id: 3,
            question: "Choose the correct spelling:",
            options: ["recieve", "receive", "receeve", "receve"],
            correctAnswer: 1
        },
        {
            id: 4,
            question: "What is the plural form of 'child'?",
            options: ["childs", "childes", "children", "child"],
            correctAnswer: 2
        },
        {
            id: 5,
            question: "Which sentence uses the correct punctuation?",
            options: [
                "Hello, how are you",
                "Hello how are you?",
                "Hello, how are you?",
                "Hello; how are you"
            ],
            correctAnswer: 2
        }
    ];

    // Test state - using reactive variables
    let currentTestType = 'placement';
    let currentQuestions = [];
    let currentQuestionIndex = 0;
    let selectedAnswers = [];
    let showResults = false;
    let showModal = false;
    let showResultsModal = false;
    let expandedSections = {0: false, 1: false, 2: false};

    // Reactive variables for UI updates
    $: currentQuestion = currentQuestions[currentQuestionIndex];
    $: progress = currentQuestions.length > 0 ? ((currentQuestionIndex + 1) / currentQuestions.length) * 100 : 0;
    $: isLastQuestion = currentQuestionIndex === currentQuestions.length - 1;
    $: canGoNext = selectedAnswers[currentQuestionIndex] !== undefined;
    $: canGoPrev = currentQuestionIndex > 0;

    // Handle keyboard events
    function handleKeydown(event) {
        if (event.key === 'Escape') {
            if (showModal) {
                closeModal();
            } else if (showResultsModal) {
                closeResultsModal();
            }
        }
    }

    // Section toggle functionality
    function toggleSection(index) {
        expandedSections[index] = !expandedSections[index];
    }

    // Modal functionality
    function openModal(testType) {
        currentTestType = testType;
        currentQuestions = testType === 'placement' ? placementQuestions : weeklyQuestions;
        currentQuestionIndex = 0;
        selectedAnswers = [];
        showResults = false;
        showModal = true;
    }

    function closeModal() {
        showModal = false;
    }

    function closeResultsModal() {
        showResultsModal = false;
    }

    function selectAnswer(answerIndex) {
        selectedAnswers[currentQuestionIndex] = answerIndex;
        // Trigger reactivity
        selectedAnswers = selectedAnswers;
    }

    function previousQuestion() {
        if (currentQuestionIndex > 0) {
            currentQuestionIndex--;
        }
    }

    function nextQuestion() {
        if (currentQuestionIndex < currentQuestions.length - 1) {
            currentQuestionIndex++;
        } else {
            showTestResults();
        }
    }

    function showTestResults() {
        const score = calculateScore();
        showModal = false;
        showResultsModal = true;
    }

    function calculateScore() {
        let correct = 0;
        selectedAnswers.forEach((answer, index) => {
            if (answer === currentQuestions[index].correctAnswer) {
                correct++;
            }
        });
        return correct;
    }

    function retakeTest() {
        closeResultsModal();
        openModal(currentTestType);
    }

    // Computed values for results
    $: score = calculateScore();
    $: percentage = currentQuestions.length > 0 ? Math.round((score / currentQuestions.length) * 100) : 0;
    $: scoreMessage = percentage >= 80 ? 'Excellent work! ðŸŽ‰' : 
                     percentage >= 60 ? 'Good job! Keep practicing! ðŸ‘' : 
                     'Keep studying and try again! ðŸ’ª';
    $: scoreClass = percentage >= 80 ? 'excellent' : 
                   percentage >= 60 ? 'good' : 
                   'needs-improvement';

