  }

  .highlight {
    background: linear-gradient(135deg, var(--primary-color), var(--primary-dark));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .hero-description {
    font-size: var(--text-lg);
    color: var(--text-secondary);
    margin-bottom: var(--spacing-8);
    line-height: 1.6;
    animation: fadeInUp 0.6s ease-out 0.2s both;
  }

  .hero-stats {
    display: flex;
    justify-content: center;
    gap: var(--spacing-8);
    margin-bottom: var(--spacing-10);
    opacity: 0;
    transform: translateY(20px);
    transition: all 0.6s ease-out;
  }

  .hero-stats.animate {
    opacity: 1;
    transform: translateY(0);
  }

  .stat-item {
    text-align: center;
  }

  .stat-number {
    font-size: var(--text-3xl);
    font-weight: 700;
    color: var(--primary-color);
    line-height: 1;
  }

  .stat-label {
    font-size: var(--text-sm);
    color: var(--text-muted);
    font-weight: 500;
    margin-top: var(--spacing-1);
  }

  .hero-actions {
    display: flex;
    gap: var(--spacing-4);
    justify-content: center;
    flex-wrap: wrap;
    animation: fadeInUp 0.6s ease-out 0.3s both;
  }

  .btn-lg {
    padding: var(--spacing-4) var(--spacing-8);
    font-size: var(--text-base);
  }

  .btn-icon {
    margin-right: var(--spacing-2);
  }

  /* Sections */
  .modules-section,
  .age-groups-section,
  .assessment-section,
  .features-section {
    padding: var(--spacing-16) 0;
  }

  .section-header {
    text-align: center;
    margin-bottom: var(--spacing-12);
  }

  .section-header h2 {
    font-size: var(--text-3xl);
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: var(--spacing-4);
  }

  .section-header p {
    font-size: var(--text-lg);
    color: var(--text-secondary);
    max-width: 600px;
    margin: 0 auto;
  }

  /* Modules Grid */
  .modules-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: var(--spacing-8);
    animation: fadeInUp 0.6s ease-out;
  }

  .module-card {
    background: var(--background-color);
    border: 1px solid var(--border-color);
    border-radius: var(--radius-xl);
    padding: var(--spacing-6);
    transition: all var(--transition-medium);
    animation: fadeInUp 0.6s ease-out;
  }

  .module-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-large);
  }

  .module-card.blue { border-left: 4px solid var(--primary-color); }
  .module-card.green { border-left: 4px solid var(--success-color); }
  .module-card.purple { border-left: 4px solid #8b5cf6; }
  .module-card.orange { border-left: 4px solid var(--warning-color); }

  .module-header {
    display: flex;
    align-items: flex-start;
    gap: var(--spacing-4);
    margin-bottom: var(--spacing-4);
  }

  .module-icon {
    font-size: 2.5rem;
    flex-shrink: 0;
  }

  .module-meta {
    flex: 1;
  }

  .module-meta h3 {
    color: var(--text-primary);
    margin-bottom: var(--spacing-1);
    font-size: var(--text-xl);
  }

  .module-weeks {
    background-color: var(--surface-color);
    color: var(--text-secondary);
    padding: var(--spacing-1) var(--spacing-3);
    border-radius: var(--radius-lg);
    font-size: var(--text-xs);
    font-weight: 500;
  }

  .difficulty-badge {
    padding: var(--spacing-1) var(--spacing-3);
    border-radius: var(--radius-lg);
    font-size: var(--text-xs);
    font-weight: 500;
    flex-shrink: 0;
  }

  .difficulty-beginner {
    background-color: var(--success-color);
    color: white;
  }

  .difficulty-intermediate {
    background-color: var(--warning-color);
    color: white;
  }

  .module-description {
    color: var(--text-secondary);
    margin-bottom: var(--spacing-5);
    line-height: 1.6;
  }

  .module-topics h4 {
    color: var(--text-primary);
    font-size: var(--text-sm);
    font-weight: 600;
    margin-bottom: var(--spacing-2);
  }

  .topics-list {
    list-style: none;
    margin: 0 0 var(--spacing-5) 0;
  }

  .topics-list li {
    color: var(--text-secondary);
    font-size: var(--text-sm);
    padding: var(--spacing-1) 0;
    position: relative;
    padding-left: var(--spacing-4);
  }

  .topics-list li::before {
    content: '‚úì';
    position: absolute;
    left: 0;
    color: var(--success-color);
    font-weight: 600;
  }

  .module-cta {
    width: 100%;
    background: linear-gradient(135deg, var(--primary-color), var(--primary-dark));
    color: white;
    border: none;
    padding: var(--spacing-3) var(--spacing-4);
    border-radius: var(--radius-md);
    font-weight: 500;
    cursor: pointer;
    transition: all var(--transition-fast);
  }

  .module-cta:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-medium);
  }

  /* Age Groups */
  .age-groups-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: var(--spacing-6);
  }

  .age-group-card {
    background: var(--background-color);
    border: 1px solid var(--border-color);
    border-radius: var(--radius-xl);
    padding: var(--spacing-6);
    text-align: center;
    transition: all var(--transition-medium);
    animation: fadeInUp 0.6s ease-out;
  }

  .age-group-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-medium);
  }

  .group-icon {
    font-size: 3rem;
    margin-bottom: var(--spacing-4);
  }

  .group-ages {
    background-color: var(--primary-color);
    color: white;
    padding: var(--spacing-2) var(--spacing-4);
    border-radius: var(--radius-lg);
    font-size: var(--text-sm);
    font-weight: 500;
    display: inline-block;
    margin-bottom: var(--spacing-4);
  }

  .group-description {
    color: var(--text-secondary);
    margin-bottom: var(--spacing-4);
  }

  .group-approach {
    background-color: var(--surface-color);
    padding: var(--spacing-4);
    border-radius: var(--radius-md);
    text-align: left;
  }

  .group-approach strong {
    color: var(--text-primary);
    display: block;
    margin-bottom: var(--spacing-2);
  }

  .group-approach span {
    color: var(--text-secondary);
    font-size: var(--text-sm);
  }

  /* Timeline */
  .timeline {
    max-width: 800px;
    margin: 0 auto;
    position: relative;
  }

  .timeline::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 2px;
    background: var(--border-color);
    transform: translateX(-50%);
  }

  .timeline-item {
    display: flex;
    align-items: center;
    margin-bottom: var(--spacing-8);
    position: relative;
    animation: fadeInUp 0.6s ease-out;
  }

  .timeline-item:nth-child(even) {
    flex-direction: row-reverse;
  }

  .timeline-marker {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background: var(--background-color);
    border: 3px solid var(--border-color);
    position: relative;
    z-index: 2;
    margin: 0 var(--spacing-6);
  }

  .timeline-item.major .timeline-marker {
    background: var(--primary-color);
    border-color: var(--primary-color);
  }

  .timeline-item.major .week-number {
    color: white;
    font-weight: 600;
  }

  .week-number {
    font-size: var(--text-sm);
    font-weight: 500;
    color: var(--text-primary);
  }

  .timeline-content {
    background: var(--background-color);
    border: 1px solid var(--border-color);
    border-radius: var(--radius-lg);
    padding: var(--spacing-4);
    min-width: 200px;
    box-shadow: var(--shadow-light);
  }

  .timeline-content h4 {
    color: var(--text-primary);
    margin-bottom: var(--spacing-2);
  }

  .timeline-content p {
    color: var(--text-secondary);
    font-size: var(--text-sm);
    margin: 0;
  }

  /* Features Grid */
  .features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--spacing-6);
  }

  .feature-card {
    text-align: center;
    padding: var(--spacing-6);
    background: var(--background-color);
    border: 1px solid var(--border-color);
    border-radius: var(--radius-xl);
    transition: all var(--transition-medium);
    animation: fadeInUp 0.6s ease-out;
  }

  .feature-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-medium);
  }

  .feature-icon {
    font-size: 3rem;
    margin-bottom: var(--spacing-4);
  }

  .feature-card h3 {
    color: var(--text-primary);
    margin-bottom: var(--spacing-3);
  }

  .feature-card p {
    color: var(--text-secondary);
    font-size: var(--text-sm);
  }

  /* CTA Section */
  .cta-section {
    background: linear-gradient(135deg, var(--primary-color), var(--primary-dark));
    color: white;
    padding: var(--spacing-16) 0;
    text-align: center;
  }

  .cta-content h2 {
    font-size: var(--text-3xl);
    margin-bottom: var(--spacing-4);
  }

  .cta-content p {
    font-size: var(--text-lg);
    margin-bottom: var(--spacing-8);
    opacity: 0.9;
  }

  .cta-actions {
    display: flex;
    gap: var(--spacing-4);
    justify-content: center;
    flex-wrap: wrap;
  }

  .btn-outline {
    background: transparent;
    color: white;
    border: 2px solid white;
  }

  .btn-outline:hover {
    background: white;
    color: var(--primary-color);
  }

  /* Animations */
  @keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  /* Responsive Design */
  @media (max-width: 768px) {
    .hero-stats {
      gap: var(--spacing-4);
    }

    .hero-title {
      font-size: var(--text-3xl);
    }

    .modules-grid {
      grid-template-columns: 1fr;
      gap: var(--spacing-6);
    }

    .timeline::before {
      left: 30px;
    }

    .timeline-item {
      flex-direction: row !important;
    }

    .timeline-marker {
      margin-left: 0;
      margin-right: var(--spacing-4);
    }

    .age-groups-grid {
      grid-template-columns: 1fr;
    }

    .features-grid {
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    }
  }

  @media (max-width: 480px) {
    .hero-stats {
      flex-wrap: wrap;
      gap: var(--spacing-3);
    }

    .stat-item {
      flex: 1;
      min-width: 80px;
    }

    .cta-actions {
      flex-direction: column;
      align-items: center;
    }
  }

