  //   const noteContent = document.getElementById(`note-${noteKey}`).value;
  //   notes = { ...notes, [noteKey]: noteContent };
  //   alert('Note saved!');
  // }
function saveNote() {
    if (currentDayKey) {
      notes = {
        ...notes,
        [currentDayKey]: tempNote
      };
       alert('Note saved!'); // Optional: Show a success message
    }
  }

  function addStudent() {
    if (studentName && studentAge) {
      students = [...students, {
        id: Date.now(),
        name: studentName,
        age: parseInt(studentAge)
      }];
      studentName = '';
      studentAge = '';
    }
  }

  function updateAttendance(studentId, status) {
    const today = new Date().toLocaleDateString();
    const key = `${studentId}_${today}`;
    attendance = { ...attendance, [key]: status };
  }

  function downloadPDF() {
    const today = new Date().toLocaleDateString();
    const doc = new jspdf.jsPDF();
    doc.text('Attendance Report', 20, 20);
    doc.text(`Date: ${today}`, 20, 30);
    doc.text('Instructor: Mr. Abdelfatah Ahmed', 20, 40);

    let y = 60;
    doc.text('Student Name', 20, y);
    doc.text('Age', 80, y);
    doc.text('Status', 120, y);

    y += 10;
    students.forEach(student => {
      const key = `${student.id}_${today}`;
      const status = attendance[key] || 'Not marked';
      doc.text(student.name, 20, y);
      doc.text(student.age.toString(), 80, y);
      doc.text(status, 120, y);
      y += 10;
    });

    doc.save(`attendance_${today.replace(/\//g, '-')}.pdf`);
  }
  beforeUpdate (() => {
      localStorage.setItem('students', JSON.stringify(students));
      localStorage.setItem('attendance', JSON.stringify(attendance));
      localStorage.setItem('notes', JSON.stringify(notes));
    });

