@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

    :global(body) {
        margin: 0;
        padding: 0;
        font-family: 'Inter', sans-serif;
        background-color: #ffffff;
        color: #333333;
        line-height: 1.6;
    }

    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    /* Header Styles */
    .header {
        background: white;
        border-bottom: 1px solid #e5e7eb;
        padding: 1rem 1.5rem;
    }

    .header-container {
        max-width: 80rem;
        margin: 0 auto;
        display: flex;
        align-items: center;
        justify-content: space-between;
    }

    .logo {
        display: flex;
        align-items: center;
        gap: 0.5rem;
    }

    .logo-icon {
        font-size: 1.25rem;
    }

    .logo-text {
        font-size: 1.25rem;
        font-weight: bold;
        color: #111827;
    }

    .nav {
        display: none;
        align-items: center;
        gap: 2rem;
    }

    .nav a {
        color: #374151;
        text-decoration: none;
        transition: color 0.2s;
    }

    .nav a:hover {
        color: #111827;
    }

    .header-actions {
        display: flex;
        align-items: center;
        gap: 1rem;
    }

    .icon-button {
        padding: 0.5rem;
        background: none;
        border: none;
        color: #6b7280;
        cursor: pointer;
        transition: color 0.2s;
    }

    .icon-button:hover {
        color: #111827;
    }

    .avatar {
        width: 2rem;
        height: 2rem;
        background: #d1d5db;
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        color: #6b7280;
    }

    @media (min-width: 768px) {
        .nav {
            display: flex;
        }
    }

    /* Instructor Profile Styles */
    .instructor-profile {
        text-align: center;
        padding: 3rem 1.5rem;
        background: #f9fafb;
    }

    .instructor-container {
        max-width: 64rem;
        margin: 0 auto;
    }

    .instructor-avatar {
